(function($) {
  "use strict"; // Start of use strict

  // Check for admin token on page load
  $(document).ready(function() {
    const token = localStorage.getItem('admin-token');
    const currentPath = window.location.pathname;

    // Don't redirect if already on login page
    if (!token && currentPath !== '/admin/login') {
      window.location.href = '/admin/login';
      return;
    }

    // If logged in and on login page, redirect to dashboard
    if (token && currentPath === '/admin/login') {
      window.location.href = '/admin/dashboard';
      return;
    }

    // Fetch current admin info if logged in
    if (token && currentPath !== '/admin/login') {
      fetchAdminInfo();
    }

    // Add active class to nav items based on current page
    addActiveClass();
  });

  // Toggle the side navigation
  $("#sidebarToggle, #sidebarToggleTop").on('click', function(e) {
    $("body").toggleClass("sidebar-toggled");
    $(".sidebar").toggleClass("toggled");
    if ($(".sidebar").hasClass("toggled")) {
      $('.sidebar .collapse').collapse('hide');
    };
  });

  // Close any open menu accordions when window is resized below 768px
  $(window).resize(function() {
    if ($(window).width() < 768) {
      $('.sidebar .collapse').collapse('hide');
    };
    
    // Toggle the side navigation when window is resized below 480px
    if ($(window).width() < 480 && !$(".sidebar").hasClass("toggled")) {
      $("body").addClass("sidebar-toggled");
      $(".sidebar").addClass("toggled");
      $('.sidebar .collapse').collapse('hide');
    };
  });

  // Prevent the content wrapper from scrolling when the fixed side navigation hovered over
  $('body.fixed-nav .sidebar').on('mousewheel DOMMouseScroll wheel', function(e) {
    if ($(window).width() > 768) {
      var e0 = e.originalEvent,
        delta = e0.wheelDelta || -e0.detail;
      this.scrollTop += (delta < 0 ? 1 : -1) * 30;
      e.preventDefault();
    }
  });

  // Scroll to top button appear
  $(document).on('scroll', function() {
    var scrollDistance = $(this).scrollTop();
    if (scrollDistance > 100) {
      $('.scroll-to-top').fadeIn();
    } else {
      $('.scroll-to-top').fadeOut();
    }
  });

  // Smooth scrolling using jQuery easing
  $(document).on('click', 'a.scroll-to-top', function(e) {
    var $anchor = $(this);
    $('html, body').stop().animate({
      scrollTop: ($($anchor.attr('href')).offset().top)
    }, 1000, 'easeInOutExpo');
    e.preventDefault();
  });

  // Logout handler
  $("#confirm-logout").on('click', function() {
    localStorage.removeItem('admin-token');
    window.location.href = '/admin/login';
  });

  // Fetch admin info
  function fetchAdminInfo() {
    const token = localStorage.getItem('admin-token');
    
    $.ajax({
      url: '/api/auth/user',
      type: 'GET',
      headers: {
        'x-auth-token': token
      },
      success: function(response) {
        if (response.success) {
          $('#current-user').text(response.user.name);
        }
      },
      error: function() {
        // If error fetching user (likely invalid token), logout
        localStorage.removeItem('admin-token');
        window.location.href = '/admin/login';
      }
    });
  }

  // Add active class to current nav item
  function addActiveClass() {
    const currentPath = window.location.pathname;
    
    // Remove active class from all nav items
    $('.nav-item').removeClass('active');
    
    // Add active class based on current path
    if (currentPath.includes('/dashboard')) {
      $('.nav-item:has(a[href="/admin/dashboard"])').addClass('active');
    } else if (currentPath.includes('/restaurants')) {
      $('.nav-item:has(a[href="/admin/restaurants"])').addClass('active');
    } else if (currentPath.includes('/orders')) {
      $('.nav-item:has(a[href="/admin/orders"])').addClass('active');
    }
  }

})(jQuery); // End of use strict

// Add AJAX request interceptor to handle authentication
$(document).ajaxError(function(event, jqXHR, settings) {
  // If any AJAX request returns 401 Unauthorized, redirect to login
  if (jqXHR.status === 401 && !settings.url.includes('/api/auth/login')) {
    localStorage.removeItem('admin-token');
    window.location.href = '/admin/login';
  }
});

